  renderLeadCount() {
    if (this.props.listleads) {
      let totalLeads = 0;
      totalLeads = this.props.listleads.leads.length;
      return (
        <Grid.Column style={styleMetrics}>
          <Header as="h4">Total Leads</Header>
          <Header style={{ margin: "0" }} as="h1">
