                <Table.Cell>{last ? last.status : null}</Table.Cell>
                <Table.Cell>{last ? last.next_steps : null}</Table.Cell>
                <Table.Cell>{last ? dateString : null}</Table.Cell>
                <Table.Cell>
                  {" "}
                  <Icon
                    onClick={() =>
                      this.setState({
                        showModal: true,
                        selectedLead: lead,
                        selectedLeadNotes: last,
                        dateString: dateString
                      })
                    }
                    name={"edit outline"}
                    size="large"
                  />
                </Table.Cell>
                <Table.Cell>
                  <Icon
                    name={"trash alternate outline"}
                    onClick={event => this.handleDeleteClick(event, lead.id)}
                    size="large"
                  />
                </Table.Cell>
              </Table.Row>
            );
          })}
        </Table.Body>
      );
    }
  }

  renderModal() {
    const { selectedLead, selectedLeadNotes, dateString } = this.state;
    const lead = selectedLead;
    const last = selectedLeadNotes;
    // const dateString = dateString;
    return (
      <Modal closeIcon onClose={this.closeModal} open={this.state.showModal}>
        <Modal.Header as="h1">
          {selectedLead.first_name + " " + selectedLead.last_name} <br />{" "}
          {lead.position ? lead.position + "," : null} {lead.company}
        </Modal.Header>
        <Modal.Header as="h3">
          Email: {lead.email}
          {/* <Icon name={"envelope"}></Icon> */}
