            </Form.Group>
            <Form.Input
              onChange={this.handleChange}
              name="commentsInput"
              control="textarea"
              rows="3"
              label="Notes"
              placeholder={last ? dateString + last.comments : null}
            />
            <Button
              style={{
                borderRadius: "50px",
                backgroundColor: "#03DAC6",
                color: "white"
              }}
              onClick={() => this.handleAddLeadNote(lead.id)}
            >
              Save
            </Button>
            {/* <Form.Description></Form.Description> */}
          </Form>
        </Modal.Content>
      </Modal>
    );
  }
  // ! /////////////////////////////////////////////////////////// RENDER START ///////////////////////////////////////////////////////////////////////////////

  render() {
    const {
      column,
      data,
      direction,
      activePage,
      filter,
