class UserProfile extends Component {
  constructor(props) {
    super();
    this.state = {
      loading: false,
      success: null
    };
  }

  componentWillMount() {
    this.props.loadUser(
      () => {
        this.setState({ loading: true });
      },
      () => {
        this.setState({ success: true, loading: false });
      },
      () => {
        this.setState({ success: false, loading: false });
      }
    );
  }

  render() {
    let condRender = null;

    if (this.state.loading) {
      condRender = <div> LOADING </div>;
    } else if (this.state.success == true) {
      condRender = (
        <div>
          {" "}
          <Navbar />
          <Header
            style={{ position: "fixed", top: "80px", left: "50px" }}
            as="h3"
            content="Text Container"
            textAlign="center"
          >
            <Grid columns={2}>
              <Grid.Column style={{ positon: "absolute", left: "100px" }}>
                <Grid.Row columns={2} doubling stackable>
                  <Grid.Column>
                    <Segment>
                      <Header as="h1">
                        {" "}
                        {this.props.auth.user.first_name}{" "}
                        {this.props.auth.user.last_name}
                      </Header>
                      <Image circular src={this.props.auth.user.img_url} />
                    </Segment>
                  </Grid.Column>
                  <Grid.Column></Grid.Column>
                </Grid.Row>
              </Grid.Column>
              <Grid.Column style={{ positon: "absolute", left: "150px" }}>
                <Grid.Row columns={3} doubling stackable>
                  <Grid.Column style={{ marginBottom: "20px" }}>
                    <Segment>Edit Account</Segment>
                  </Grid.Column>
                  <Grid.Column style={{ marginBottom: "20px" }}>
                    <Segment>View Lead Lists</Segment>
                  </Grid.Column>
                  <Grid.Column style={{ marginBottom: "20px" }}>
                    <Segment>Delete Account</Segment>
                  </Grid.Column>
                </Grid.Row>
              </Grid.Column>
            </Grid>
          </Header>
        </div>
      );
    } else if (!this.state.success) {
      condRender = <div> ERROR </div>;
    }

    return condRender;
  }
}

