import React, { Component } from "react";
import { Button, Header, Image, Modal, Card, Form, Dropdown } from "semantic-ui-react";


const styleDropdown = {
  width: "40%"
};

const statusArray = [
{key: "10", text: "Meeting Booked", value: "meeting booked"},
{key: "25", text: "Meeting Booked", value: "meeting booked"},
{key: "50", text: "Meeting Booked", value: "meeting booked"},
{key: "90", text: "Meeting Booked", value: "meeting booked"},
{key: "10", text: "Meeting Booked", value: "meeting booked"},
{key: "10", text: "Meeting Booked", value: "meeting booked"},
{key: "10", text: "Meeting Booked", value: "meeting booked"},
{key: "10", text: "Meeting Booked", value: "meeting booked"},

]

export default class LeadNoteModal extends Component {

  render() {
    return (
      <div>
        <Modal trigger={<Button>Show Modal</Button>}>
          <Modal.Header>Select a Photo</Modal.Header>
          <Modal.Content image>
            <Modal.Description>
              <Header>LEAD DETAILS</Header>
            </Modal.Description>
            <Form.Group>
              <Dropdown
                onChange={this.handleDropdown}
                name="listId"
                style={styleDropdown}
                placeholder="Select list..."
                fluid
                selection
                options={statusArray}
              />
              <Form.Field>
                <label>First Name</label>
                <input placeholder="First Name" />
              </Form.Field>
              <Form.TextArea
                label="About"
                placeholder="Tell us more about you..."
              />
            </Form.Group>
          </Modal.Content>
        </Modal>
      </div>
    );
  }
}
