import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment
} from "semantic-ui-react";
import { Link, NavLink } from "react-router-dom";
import NewUserForm from "./NewUserForm";
const loginURL = "http://localhost:3000/login"; // Host

const styleColumn = {
  float: "left",
  width: "50%",
  padding: "14px"
  // margin: '10px'
};

const styleForm = {
  float: "right",
  width: "50%",
  padding: "20px",
  paddingLeft: "20px"
};

export default class LoginForm extends Component {
  state = {
    emailInput: "",
    passwordInput: ""
  };

  handleSubmit = () => {
    fetch(loginURL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        email: this.state.emailInput,
        password: this.state.passwordInput
      })
    })
      .then(res => res.json())
      .then(data => {
        if (Object.keys(data)[0] !== "error") {
          localStorage.setItem("token", data.jwt);
          this.props.getUser(data.user);
          this.props.fetchUsers();
          this.props.fetchTeams();
        } else {
          console.log(data);
        }
      });
    // .then(userdata => this.props.getUserId(userdata.results))
  };

  handleChange = e => {
    const targetValue = e.target.value;
    const targetName = e.target.name;
    this.setState({ [targetName]: targetValue });
  };

  render() {
    console.log(this.state);
    return (
      <div className="login-form">
        <Grid>
          <Grid.Column style={styleColumn}>
            <Image
              src="https://scontent-ort2-2.xx.fbcdn.net/v/t1.15752-9/70899389_2519172824806568_6105558141708009472_n.png?_nc_cat=109&_nc_oc=AQl7M70Sg3NpW0mxFQ2HiBBb2vaWR7OI91ePG62W4zv7EYNgY-_2YGp5fZ2Vio2dVgE&_nc_ht=scontent-ort2-2.xx&oh=83ca814288ac01ffb3a5c2a9f90c506f&oe=5DFE3575"
              width="100%"
              height="100%"
            />
          </Grid.Column>
          <Grid.Column
            style={styleForm}
            textAlign="center"
            verticalAlign="middle"
          >
            <Header as="h2" style={{ color: "#03DAC6" }} textAlign="center">
              Log-in
            </Header>
            <Form size="large">
              <Segment>
                <Form.Input
                  onChange={this.handleChange}
                  name="emailInput"
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  onChange={this.handleChange}
                  name="passwordInput"
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />
                <Button
                  style={{ backgroundColor: "#03DAC6", color: "white" }}
                  fluid
                  size="large"
                >
                  Login
                </Button>
              </Segment>
            </Form>
            <NavLink to="/signup" activeClassName="hurray">
              New User
            </NavLink>
          </Grid.Column>
        </Grid>
        )
      </div>
    );
  }
}
