import React, { Component } from 'react';
import SearchBar from '../components/SearchBar'
import SearchResults from '../components/SearchResults'
import SearchResultsTest from '../components/SearchResultsTest'
import { thunkFetchLeads } from "../actions";
import { connect } from "react-redux";

const CATEGORY_URL = "http://localhost:3000/api/v1/categories";
const LEADS_URL =  "http://localhost:3000/api/v1/leads"


class SearchContainer extends Component {

  componentDidMount() {
    // this.fetchCategories()
    // this.props.thunkFetchCategories()
    // this.fetchQuery()
    this.fetchLeads()
  }

  fetchLeads = () => {
    fetch(LEADS_URL)
    .then(res => res.json())
    .then(data => this.setState({leadsResults: data}))
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <SearchBar />
        {/* <SearchResults /> */}
        {this.state.leadsResults ? 
        <SearchResultsTest leadsResults={this.state.leadsResults}/> : "loading"}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {leads: state.leads}
}

const mapDispatchToProps = dispatch => {
  return {}
}


export default connect(mapStateToProps, mapDispatchToProps)(SearchContainer);
