import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment
} from "semantic-ui-react";
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import SearchContainer from "../containers/SearchContainer";

const loginURL = "http://localhost:3000/login"; // Host

const styleColumn = {
  float: "left",
  width: "50%",
  padding: "14px"
  // margin: '10px'
};

const styleForm = {
  float: "right",
  width: "50%",
  padding: "20px",
  paddingLeft: "20px"
};

class LoginForm extends Component {
  state = {
    emailInput: "",
    passwordInput: ""
  };

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    this.props.registerUser(this.state, this.props.history);
  };

  render() {
    console.log(this.state);
    return (
      <div className="login-form">
      {!this.props.auth.id ? 
        <Grid>
          <Grid.Column style={styleColumn}>
            <Image
              src="https://scontent-ort2-2.xx.fbcdn.net/v/t1.15752-9/70899389_2519172824806568_6105558141708009472_n.png?_nc_cat=109&_nc_oc=AQl7M70Sg3NpW0mxFQ2HiBBb2vaWR7OI91ePG62W4zv7EYNgY-_2YGp5fZ2Vio2dVgE&_nc_ht=scontent-ort2-2.xx&oh=83ca814288ac01ffb3a5c2a9f90c506f&oe=5DFE3575"
              width="100%"
              height="100%"
            />
          </Grid.Column>
          <Grid.Column
            style={styleForm}
            textAlign="center"
            verticalAlign="middle"
          >
            <Header as="h2" style={{ color: "#03DAC6" }} textAlign="center">
              Log-in
            </Header>
            <Form size="large">
              <Segment>
                <Form.Input
                  onChange={this.handleChange}
                  name="emailInput"
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  onChange={this.handleChange}
                  name="passwordInput"
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />
                <Button
                  style={{ backgroundColor: "#03DAC6", color: "white" }}
                  fluid
                  size="large"
                >
                  Login
                </Button>
              </Segment>
            </Form>
            <NavLink to="/signup" activeClassName="hurray">
              New User
            </NavLink>
          </Grid.Column>
        </Grid> : <SearchContainer /> }
      </div> 
    );
  }
}

const mapStateToProps = state => {
  return { 
    auth: state.auth
  }
}

const mapDispatchToProps = dispatch => {
  return {
    login: (formData, history) => {
      dispatch(login(formData, history))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(LoginForm))
