import React, { Component } from "react";
import { Form, Dropdown, Button } from "semantic-ui-react";
import {thunkFetchCategories} from '../actions'

/////////////////////// STYLING /////////////////////////
const searchOptions = [
  { key: "industry", text: "Industry", value: "industry" },
  { key: "domain", text: "Company Domain", value: "domain" }
];

const styleDropdown = {
  width: "20%"
};

const CATEGORY_URL = 'http://localhost:3000/api/v1/categories'


/////////////////////// STYLING /////////////////////////

class SearchBar extends Component {
  state = {
    selectionParam: "",
    searchParam: "",
    locationParam: ""
  };

  componentDidMount() {
    this.fetchCategories()
  }


  handleDropdown = (e, data) => {
    const targetValue = data.value;
    const targetName = data.name;
    this.setState({ [targetName]: targetValue });
  };

  handleChange = e => {
    const targetValue = e.target.value;
    const targetName = e.target.name;
    this.setState({ [targetName]: targetValue });
  };

  handleSubmit = () => {
    // ACTION - QUERYSEARCH
  };

  fetchCategories = () => {
    fetch(CATEGORY_URL)
    .then(res => res.json())
    .then(console.log)
  }

  fetchQuery = () => {};

  render() {
    console.log(this.state);
    return (
      <div>
        <Form>
          <Dropdown
            onChange={this.handleDropdown}
            name="selectionParam"
            style={styleDropdown}
            placeholder="Search by..."
            fluid
            selection
            options={searchOptions}
          />
          {this.state.selectionParam === "industry" ? (
            <Form>
              <Form.Input
                onChange={this.handleChange}
                name="searchParam"
                fluid
                placeholder="Industry"
              />
              <Form.Input
                onChange={this.handleChange}
                name="locationParam"
                fluid
                placeholder="Enter city name..."
              />
            </Form>
          ) : (
            <Form.Input
            onChange={this.handleChange}
            name="searchParam"
            fluid
            placeholder="Enter domain name..."
          />
          )}
        </Form>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    thunkFetchCategories: () => {
      dispatch(thunk)
    }
  }
}


export default connect(null, mapDispatchToProps)(SearchBar);


