import React from "react";
import { NavLink, Redirect, Link } from "react-router-dom";
import { logoutUser } from "../actions";
import { connect } from "react-redux";
import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const styleBar = {
  overflow: "hidden",
  backgroundColor: "#5400E8"
  // position: 'fixed',
  // top: '0',
  // width: '100%'
};

const styleLink = {
  float: "right",
  display: "block",
  color: "#f2f2f2",
  textAlign: "center",
  padding: "14px 16px",
  fontSize: "17px",
  textDecoration: "none"
};

class Navbar extends React.Component {
  state = {}

  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })

  render() {
    const { children } = this.props
    const { fixed } = this.state

    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            inverted
            textAlign='center'
            style={{ minHeight: 700, padding: '1em 0em' }}
            vertical
          >
            <Menu
              fixed={fixed ? 'top' : null}
              inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size='large'
            >
              <Container>
                <Menu.Item as={Link} to='/' active>
                  Home
                </Menu.Item>
                <Menu.Item as={Link} to='/leadlists'>Dashboard</Menu.Item>
                <Menu.Item as={Link} to='/profile'>My Profile</Menu.Item>
                <Menu.Item as={Link} to='/results'>Results</Menu.Item>
                <Menu.Item position='right'>
                  <Button as={Link} to='/login' inverted={!fixed}>
                    Log in
                  </Button>
                  <Button as={Link} to='/signup' inverted={!fixed} primary={fixed} style={{ marginLeft: '0.5em' }}>
                    Sign Up
                  </Button>
                </Menu.Item>
              </Container>
            </Menu>
            <HomepageHeading />
          </Segment>
        </Visibility>

        {children}
      </Responsive>
    )
  }
}



const mapDispatchToProps = (dispatch) => {
  return {
    logoutUser: () => dispatch(logoutUser())
  };
}

export default connect(
  null,
  mapDispatchToProps
)(Navbar);


