import React, { Component } from "react";
import { Table, Menu, Icon, Modal, Header, Button } from "semantic-ui-react";
import { connect } from "react-redux";

class SearchResults extends Component {
  render() {
    const dataArray = this.props.leads[0];
    console.log(dataArray);
    const tableRow = dataArray.emails.map(lead => {
      return (
        <Table.Row>
          <Table.Cell>{lead.first_name}</Table.Cell>
          <Table.Cell>{lead.last_name}</Table.Cell>
          <Table.Cell>{lead.value}</Table.Cell>
          <Table.Cell>{lead.position}</Table.Cell>
          <Table.Cell>{dataArray.organization}</Table.Cell>
          <Table.Cell>{lead.confidence}</Table.Cell>
          <Table.Cell>
            {" "}
            <Modal trigger={<Button>Basic Modal</Button>} basic size="small">
              <Header icon="archive" content="Archive Old Messages" />
              <Modal.Content>
                <p>
                  Your inbox is getting full, would you like us to enable
                  automatic archiving of old messages?
                </p>
              </Modal.Content>
              <Modal.Actions>
                <Button basic color="red" inverted>
                  <Icon name="remove" /> No
                </Button>
                <Button color="green" inverted>
                  <Icon name="checkmark" /> Yes
                </Button>
              </Modal.Actions>
            </Modal>
          </Table.Cell>
        </Table.Row>
      );
    });
    return (
      <div>
        <Table singleLine>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>First Name</Table.HeaderCell>
              <Table.HeaderCell>Last Name</Table.HeaderCell>
              <Table.HeaderCell>Email</Table.HeaderCell>
              <Table.HeaderCell>Position</Table.HeaderCell>
              <Table.HeaderCell>Company</Table.HeaderCell>
              <Table.HeaderCell>Confidence</Table.HeaderCell>
              <Table.HeaderCell>Save Lead</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>{tableRow}</Table.Body>
          <Table.Footer>
            <Table.Row>
              <Table.HeaderCell colSpan="3">
                <Menu floated="right" pagination>
                  <Menu.Item as="a" icon>
                    <Icon name="chevron left" />
                  </Menu.Item>
                  <Menu.Item as="a">1</Menu.Item>
                  <Menu.Item as="a">2</Menu.Item>
                  <Menu.Item as="a">3</Menu.Item>
                  <Menu.Item as="a">4</Menu.Item>
                  <Menu.Item as="a" icon>
                    <Icon name="chevron right" />
                  </Menu.Item>
                </Menu>
              </Table.HeaderCell>
            </Table.Row>
          </Table.Footer>
        </Table>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { leads: state.leads };
};

// Connect this component to all returns for the company search
export default connect(
  mapStateToProps,
  null
)(SearchResults);
