import PropTypes from "prop-types";
import { Link, withRouter } from "react-router-dom";
import React, { Component } from "react";
import {
  Button,
  Container,
  Header,
  Icon,
  Loader,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
  Grid, 
  Image,
  Divider,
  List
} from "semantic-ui-react";
import SearchBar from "./SearchBar";
import { connect } from "react-redux";
import { logoutUser, currentUser } from "../actions";

// Heads up!
// We using React Static to prerender our docs with server side rendering, this is a quite simple solution.
// For more advanced usage please check Responsive docs under the "Usage" section.
const getWidth = () => {
  const isSSR = typeof window === "undefined";
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth;
};

const HomepageHeading = ({ mobile }) => (
  <Container text centered>
    <Header
      as="h1"
      content="Search for Prospects Instantly"
      style={{
        fontSize: mobile ? "2em" : "3.2em",
        fontWeight: "bold",
        marginBottom: "10px",
        marginTop: mobile ? "1.5em" : "4.5em"
      }}
    />
    <SearchBar />
  </Container>
);

HomepageHeading.propTypes = {
  mobile: PropTypes.bool
};

class DesktopContainer extends Component {
  state = {
    logoutChange: false
  };

  componentDidMount = () => {
    this.props.currentUser(this.props.history)
  }

  hideFixedMenu = () => this.setState({ fixed: false });
  showFixedMenu = () => this.setState({ fixed: true });

  handleLogout = () => {
    this.props.logoutUser();
    localStorage.removeItem("token");
    this.setState({ logoutChange: true });
    // this.props.history.push("/");
    // redirect to landing page
  };

  render() {
    const { children } = this.props;
    const { fixed } = this.state;

    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Segment
            // inverted
            textAlign="center"
            // style={{ minHeight: 700, padding: "1em 0em" }}
            vertical
            style={{
              backgroundImage: `url(${"https://scontent-ort2-2.xx.fbcdn.net/v/t1.15752-9/s2048x2048/71093458_463527317706998_6857018496128122880_n.png?_nc_cat=101&_nc_oc=AQl2gDIEaIvqJ9nlneGMjfaDHtgfbFjLjkXKrF1ATz_lG8I8Qq2SYVjDCYwbysjSCwM&_nc_ht=scontent-ort2-2.xx&oh=644556da3c91d328452fcb67714c1c7d&oe=5E3A8CD8"})`,
              backgroundSize: "cover",
              minHeight: "1000px",
              padding: "1em 0em"
            }}
          >
            <Menu
              fixed={fixed ? "top" : null}
              // inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size="large"
            >
              <Container>
                <Menu.Item
                  style={{ fontSize: "large", color: "#43425D" }}
                  as={Link}
                  to="/"
                  active
                >
                  Home
                </Menu.Item>
                <Menu.Item
                  style={{ fontSize: "large", color: "#43425D" }}
                  as={Link}
                  to={this.props.auth.user ? "/leadlists" : "/login" }
                >
                  Dashboard
                </Menu.Item>
                <Menu.Item
                  style={{ fontSize: "large", color: "#43425D" }}
                  as={Link}
                  to={this.props.auth.user ? "/profile" : '/login' }
                >
                  My Account
                </Menu.Item>
                {this.props.auth.user ? (
                  <Menu.Item position="right">
                    <Button
                      onClick={() => this.handleLogout()}
                      basic
                      color="grey"
                      primary={fixed}
                      style={{
                        backgroundColor: "#03DAC6",
                        color: "white",
                        marginLeft: "1em"
                      }}
                    >
                      Logout
                    </Button>
                  </Menu.Item>
                ) : (
                  <Menu.Item position="right">
                    <Button
                      as={Link}
                      to="/login"
                      style={{ backgroundColor: "#6200EE", color: "white" }}
                    >
                      Log in
                    </Button>
                    <Button
                      as={Link}
                      to="/signup"
                      // basic
                      style={{
                        backgroundColor: "#03DAC6",
                        color: "white",
                        marginLeft: "0.5em"
                      }}
                      primary={fixed}
                    >
                      Sign Up
                    </Button>
                  </Menu.Item>
                )}
              </Container>
            </Menu>
            <HomepageHeading />
         <HomepageLayout />
          </Segment>
        </Visibility>

        {children}
      </Responsive>
    );
  }
}

DesktopContainer.propTypes = {
  children: PropTypes.node
};

