import React, { Component } from 'react'

const categoryURL = "https://www.yelp.com/developers/documentation/v3/all_category_list/categories.json"

export default class App extends Component {
  state = {
    categories: [],
    domainInput: "",
    domainQueryResults: []
  }

componentDidMount() {
  this.fetchHunter()
}


fetchHunter = () => {
  const domain = this.state.domainInput
  const hunterAPI = `https://api.hunter.io/v2/domain-search?domain=${domain}&api_key=7ca084937e5e049696b7bb64c10366c3d077c650`
  fetch(hunterAPI)
    .then(res => res.json())
    .then(data => this.setState({domainQueryResults: data}))
    .catch(err => console.log(err))
}


  render() {
