import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment
} from "semantic-ui-react";
import { Link } from "react-router-dom";
// import NewUserForm from "./NewUserForm";
const loginURL = "http://localhost:3000/login"; // Host
// const style = {
//   backgroundColor: "#D9D9D9",
//   height: "100vh"
//   // margin: "0 15px"
// }

const styleColumn = {
  float: "left",
  width: "50%",
  padding: "5px"
};

const styleForm = {
  float: "right",
  width: "50%",
  padding: "30px"
}

export default class LoginForm extends Component {
  state = {
    usernameInput: "",
    passwordInput: ""
  };

  handleSubmit = () => {
    fetch(loginURL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        username: this.state.usernameInput,
        password: this.state.passwordInput
      })
    })
      .then(res => res.json())
      .then(data => {
        if (Object.keys(data)[0] !== "error") {
          localStorage.setItem("token", data.jwt);
          this.props.getUser(data.user);
          this.props.fetchUsers();
          this.props.fetchTeams();
        } else {
          console.log(data);
        }
      });
    // .then(userdata => this.props.getUserId(userdata.results))
  };

  handleChange = e => {
    const targetValue = e.target.value;
    const targetName = e.target.name;
    this.setState({ [targetName]: targetValue });
  };

  render() {
    // console.log(this.state)
    return (
      <div className="login-form">
        {/* <Grid textAlign="center" style={style} verticalAlign="middle"> */}
        <Grid>
          <Grid.Column style={styleColumn}>
            <Image
              src="https://i.pinimg.com/564x/2d/ca/5b/2dca5beda58a443ae7de94860da4b610.jpg"
              width="100%" height="100%"
            />
          </Grid.Column>
          <Grid.Column style={styleForm} textAlign='center' verticalAlign='middle'>
            <Header as="h2" style={{color: "#03DAC6"}} textAlign="center">
              Log-in to your account
            </Header>
            <Form size="large">
              <Segment>
                <Form.Input
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />

                <Button style={{backgroundColor: "#03DAC6", color: 'white'}} fluid size="large">
                  Login
                </Button>
              </Segment>
            </Form>
            <Message>
              New to us? <a href="#">Sign Up</a>
            </Message>
          </Grid.Column>
        </Grid>
        )
      </div>
    );
  }
}
