import React from "react";
import PropTypes from "prop-types";
import { NavLink, Redirect, Link, withRouter } from "react-router-dom";
import { logoutUser, clearMessage } from "../actions";
import { connect } from "react-redux";
import { Header, Image } from "semantic-ui-react";

const styleBar = {
  overflow: "hidden",
  // backgroundColor: '#5400E8',
  margin: "10px",
  height: "50px",
  textAlign: "center",
  verticalAlign: "middle",
  position: "fixed",
  opacity: "1",
  zIndex: "1",
  top: "0",
  width: "100%"
};

const styleLinkLeft = {
  marginLeft: "10px",
  float: "left",
  display: "block",
  color: "#6200EE",
  textAlign: "center",
  padding: "14px 16px",
  fontSize: "17px",
  textDecoration: "none",
  marginLeft: "20px"
};

const styleLinkRight = {
  float: "right",
  display: "block",
  color: "#6200EE",
  textAlign: "center",
  padding: "14px 16px",
  fontSize: "17px",
  textDecoration: "none"
};

class Navbar extends React.Component {
  handleLogout = () => {
    this.props.logoutUser();
    localStorage.removeItem("token");
    this.props.history.push("/");
    // redirect to landing page
  };

  handleClearMessage = () => {
    this.props.clearMessage();
  };

  render() {
    return (
      <div style={styleBar}>

<Menu
              fixed={fixed ? "top" : null}
              // inverted={!fixed}
              pointing={!fixed}
              secondary={!fixed}
              size="large"
            >
              <Container>
                <Menu.Item
                  style={{ fontSize: "large", color: "#43425D" }}
                  as={Link}
                  to="/"
                  active
                  onClick={this.props.clearMessage}
                >
                  Home
                </Menu.Item>
                <Menu.Item
                  style={{ fontSize: "large", color: "#43425D" }}
                  as={Link}
                  to={this.props.auth.user ? "/dashboard" : "/login" }
                  onClick={this.props.clearMessage}
                >
                  Dashboard
                </Menu.Item>
                <Menu.Item
                  style={{ fontSize: "large", color: "#43425D" }}
                  as={Link}
                  to={this.props.auth.user ? "/profile" : '/login' }
                  onClick={this.props.clearMessage}
                >
                  My Account
                </Menu.Item>
                {this.props.auth.user ? (
                  <Menu.Item position="right">
                    <Button
                      onClick={() => this.handleLogout()}
                      // basic
                      // color="grey"
                      primary={fixed}
                      style={{
                        backgroundColor: "#03DAC6",
                        color: "white",
                        marginLeft: "1em"
                      }}
                    >
                      Logout
                    </Button>
                  </Menu.Item>
                ) : (
                  <Menu.Item position="right">
                    <Button
                      as={Link}
                      to="/login"
                      style={{ backgroundColor: "#6200EE", color: "white" }}
                    >
                      Log in
                    </Button>
                    <Button
                      as={Link}
                      to="/signup"
                      // basic
                      style={{
                        backgroundColor: "#03DAC6",
                        color: "white",
                        marginLeft: "0.5em"
                      }}
                      primary={fixed}
                    >
                      Sign Up
                    </Button>
                  </Menu.Item>
                )}
              </Container>
            </Menu>


        <div className="style-links" style={{ marginRight: "50px" }}>
          <NavLink
            onClick={this.handleClearMessage}
            style={styleLinkLeft}
            to="/"
          >
            Home
          </NavLink>
          <NavLink
            onClick={this.handleClearMessage}
            style={styleLinkLeft}
            to="/dashboard"
          >
            Dashboard
          </NavLink>
          {/* <NavLink onClick={this.handleClearMessage} style={styleLinkLeft} to="/profile">
          My Profile
        </NavLink> */}
          {/* <Image style={{display: 'inline-block', float: 'right', margin: '10px', right: 140}} circular size='mini' src={this.props.auth.user.img_url}></Image> */}
          <NavLink style={styleLinkRight} onClick={() => this.handleLogout()}>
            Logout
          </NavLink>
          <NavLink
            onClick={this.handleClearMessage}
            style={styleLinkRight}
            to="/profile"
          >
            {this.props.auth.user.first_name}
          </NavLink>
          <NavLink>
            {" "}
            <Image
              style={{
                display: "inline-block",
                float: "right",
                margin: "10px",
                marginRight: '10px'
                // right: 140
              }}
              circular
              size="mini"
              src={this.props.auth.user.img_url}
            ></Image>
          </NavLink>
        </div>


      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    auth: state.auth
  };
};

export default connect(
  mapStateToProps,
  { logoutUser, clearMessage }
)(withRouter(Navbar));
