import React, { Component } from "react";
import Navbar from "./Navbar";
import { Header, Image, Segment, Container, Grid} from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import {currentUser} from '../actions'

class UserProfile extends Component {

  state = {
    isLoading: true
  }

  componentDidMount() {
    this.props.currentUser(this.props.history)
    this.setState({isLoading: false})
    console.log("it worked")
  }

  render() {
    console.log(this.state)
    const {img_url, first_name, last_name, email} = this.props.auth.user
    if (!this.props.auth.user) {
      return (
        <div>Loading...</div>
      )
    } else {
    return (
      <div>
        <Navbar />
        <Header
          style={{ position: "fixed", top: "80px", left: "50px" }}
          as="h3"
          content="Text Container"
          textAlign="center"
        >
          <Grid columns={2}>
            <Grid.Column>
              <Grid columns={2} doubling stackable>
                <Grid.Column>
                  <Segment>
                    <Image circular src={img_url} />
                  </Segment>
                </Grid.Column>
                <Grid.Column>
                  <Segment>{first_name}</Segment>
                </Grid.Column>
              </Grid>
            </Grid.Column>
            <Grid.Column>
              <Grid columns={3} doubling stackable>
                <Grid.Column>
                  <Segment>Content</Segment>
                </Grid.Column>
                <Grid.Column>
                  <Segment>Content</Segment>
                </Grid.Column>
                <Grid.Column>
                  <Segment>Content</Segment>
                </Grid.Column>
              </Grid>
            </Grid.Column>
          </Grid>
        </Header>
      </div>
    );
    }
  }
}

const mapStateToProps = state => {
  return {
    auth: state.auth
  };
};

export default connect(
  mapStateToProps,
  {currentUser}
)(withRouter(UserProfile));
