    });
    return (
      <div>
        <Navbar />
        <div>
          <h1>
            SEARCH RESULTS
          </h1>
        </div>
        <Table singleLine>
          <Table.Header>
            {this.state.leadsArray.length > 0 ? (
              <Table.Row>
                <Table.HeaderCell colSpan="6">
                  {this.state.leadsArray.length} leads selected
                </Table.HeaderCell>
                <Table.HeaderCell>
                  <Modal
                    centered
                    trigger={
                      <Button onClick={this.handleLeadChange}>
                        Save Leads
                      </Button>
                    }
                    basic
                    size="small"
                  >
                    {Object.keys(lists).length > 1 ? (
                      <div>
                        <Modal.Header as="h2">
