import React, { Component } from "react"
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment
} from "semantic-ui-react"
import { Link } from "react-router-dom"
// import NewUserForm from "./NewUserForm";
const loginURL = "http://localhost:3000/login" // Host
const style = {
  backgroundColor: "#D9D9D9",
  height: "100vh"
  // margin: "0 15px"
}

export default class LoginForm extends Component {
  state = {
    usernameInput: "",
    passwordInput: ""
  }

  handleSubmit = () => {
    fetch(loginURL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        username: this.state.usernameInput,
        password: this.state.passwordInput
      })
    })
      .then(res => res.json())
      .then(data => {
        if (Object.keys(data)[0] !== 'error') {
            localStorage.setItem("token", data.jwt)
            this.props.getUser(data.user)
            this.props.fetchUsers()
            this.props.fetchTeams()
        } else {
            console.log(data)
        }
      })
      // .then(userdata => this.props.getUserId(userdata.results))
  }

  handleChange = e => {
    const targetValue = e.target.value
    const targetName = e.target.name
    this.setState({ [targetName]: targetValue })
  }

  render() {
    // console.log(this.state)
    return (
      <div style={style} className="login-form">
        {/* <Grid textAlign="center" style={style} verticalAlign="middle"> */}
          <Grid>
          <Grid.Column width={8}>
          <Image src='https://i.pinimg.com/564x/2d/ca/5b/2dca5beda58a443ae7de94860da4b610.jpg' />
          </Grid.Column>
          <Grid.Column width={8}>
            <Form size="large" onSubmit={this.handleSubmit}>
              <Segment stacked>
                <Header as="h1">
                    ROULETTE
                </Header>
                <Form.Input
                  onChange={this.handleChange}
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="Username"
                  type="text"
                  name="usernameInput"
                />
                <Form.Input
                  onChange={this.handleChange}
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                  name="passwordInput"
                />
                <Button
                  type="submit"
                  style={{ backgroundColor: "#F9CF00" }}
                  fluid
                  size="large"
                >
                  Login
                </Button>
              </Segment>
            </Form>
            <Message>
              <Link to="/createaccount">Create Account</Link>
            </Message>
          </Grid.Column>
        </Grid>
        )
      </div>
    )
  }
}
