import React, { Component } from "react";
import { Form, Dropdown, Button } from "semantic-ui-react";
import { thunkFetchCategories } from "../actions";
import { connect } from "react-redux";

var HunterSDK = require("hunterio");

var KEY = "7ca084937e5e049696b7bb64c10366c3d077c650";

var hunter = new HunterSDK(KEY);

/////////////////////// STYLING /////////////////////////
const searchOptions = [
  { key: "industry", text: "Industry", value: "industry" },
  { key: "domain", text: "Company Domain", value: "domain" }
];

const styleDropdown = {
  width: "20%"
};

const CATEGORY_URL = "http://localhost:3000/api/v1/categories";
const LEADS_URL =  "http://localhost:3000/api/v1/leads"

/////////////////////// STYLING /////////////////////////

class SearchBar extends Component {
  state = {
    selectionParam: "",
    searchParam: "",
    locationParam: ""
  };

  componentDidMount() {
    // this.fetchCategories()
    // this.props.thunkFetchCategories()
    // this.fetchQuery()

  }

  handleDropdown = (e, data) => {
    const targetValue = data.value;
    const targetName = data.name;
    this.setState({ [targetName]: targetValue });
  };

  handleChange = e => {
    const targetValue = e.target.value;
    const targetName = e.target.name;
    this.setState({ [targetName]: targetValue });
  };

  handleSubmit = () => {
    // ACTION - QUERYSEARCH

  };

  fetchLeads = () => {
    fetch(LEADS_URL)
    .then(res => res.json())
    .then(console.log)
  }

  fetchQuery = () => {
    hunter.domainSearch(
      {domain: "fonterra.com"},
      function(err, body) {
        if (err) {
          console.log(err);
        } else {
          // Will contain same body as the raw API call
          console.log(body);
        }
      }
    );
  };

  // fetchHunter = () => {
  //   fetch("https://api.hunter.io/v2/domain-search?domain=stripe.com&api_key=7ca084937e5e049696b7bb64c10366c3d077c650")
  //   .then(res => res.json())
  //   .then(console.log)
  // }

  render() {
    console.log(this.state);
    return (
      <div>
        <Form>
          <Dropdown
            onChange={this.handleDropdown}
            name="selectionParam"
            style={styleDropdown}
            placeholder="Search by..."
            fluid
            selection
            options={searchOptions}
          />
          {this.state.selectionParam === "industry" ? (
            <Form>
              <Form.Input
                onChange={this.handleChange}
                name="searchParam"
                fluid
                placeholder="Industry"
              />
              <Form.Input
                onChange={this.handleChange}
                name="locationParam"
                fluid
                placeholder="Enter city name..."
              />
              <Button type="submit">Search</Button>
            </Form>
          ) : (
            <Form>
              <Form.Input
                onChange={this.handleChange}
                name="searchParam"
                fluid
                placeholder="Enter domain name..."
              />
              <Button type="submit" onSubmit={this.handleSubmit}>
                Search
              </Button>
            </Form>
          )}
        </Form>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    thunkFetchCategories: () => {
      dispatch(thunkFetchCategories());
    }
  };
};

const mapStateToProps = state => {
  return {
    categories: state.categories
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(SearchBar);

