import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment
} from "semantic-ui-react";
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import SearchContainer from "../containers/SearchContainer";
import { login } from "../actions";
import "../App.css";

// const styleColumn = {
//   float: "left",
//   width: "50%",
//   padding: "14px"
//   // margin: '10px'
// };

const styleForm = {
  // float: "right",
  width: "500px",
  height: "200px",
  padding: "30px",
  margin: "40px",
  textAlign: 'center'
  // paddingLeft: "20px"
};

class LoginForm extends Component {
  state = {
    email: "",
    password: ""
  };

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    this.props.login(this.state, this.props.history);
  };

  render() {
    console.log(this.state);
    return (
      <div className="login-form">
        {!this.props.auth.id ? (
          <Grid textAlign="center" verticalAlign="middle">
            {/* <Grid.Column style={styleColumn}>
            <Image
              src="https://scontent-ort2-2.xx.fbcdn.net/v/t1.15752-9/70899389_2519172824806568_6105558141708009472_n.png?_nc_cat=109&_nc_oc=AQl7M70Sg3NpW0mxFQ2HiBBb2vaWR7OI91ePG62W4zv7EYNgY-_2YGp5fZ2Vio2dVgE&_nc_ht=scontent-ort2-2.xx&oh=83ca814288ac01ffb3a5c2a9f90c506f&oe=5DFE3575"
              width="100%"
              height="100%"
            />
          </Grid.Column> */}
            {/* <Grid.Column
            style={styleForm}
            textAlign="center"
            verticalAlign="middle"
          > */}
            <Form.Group>
              <Form style={styleForm} size="large" onSubmit={this.handleSubmit}>
                <Header as="h2" style={{ color: "#03DAC6" }} textAlign="center">
                  Log-in
                </Header>
                <Segment>
                  <Form.Input
                    onChange={this.handleChange}
                    name="email"
                    fluid
                    icon="user"
                    iconPosition="left"
                    placeholder="E-mail address"
                  />
                  <Form.Input
                    onChange={this.handleChange}
                    name="password"
                    fluid
                    icon="lock"
                    iconPosition="left"
                    placeholder="Password"
                    type="password"
                  />
                  <Button
                    style={{ backgroundColor: "#03DAC6", color: "white" }}
                    fluid
                    size="large"
                  >
                    Login
                  </Button>
                  <NavLink
                    style={{marginTop: '20px'}}
                    to="/signup"
                    activeClassName="hurray"
                  >
                    New User
                  </NavLink>
                </Segment>
              </Form>
            </Form.Group>
            {/* </Grid.Column> */}
          </Grid>
        ) : (
          <SearchContainer />
        )}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    auth: state.auth
  };
};

const mapDispatchToProps = dispatch => {
  return {
    login: (formData, history) => {
      dispatch(login(formData, history));
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(LoginForm));
