import { combineReducers } from 'redux'
import { FETCH_CATEGORIES, FETCH_LEADS, FETCH_LISTS, ADD_LIST, LOGIN_USER, LOGOUT_USER, DELETE_LIST } from '../actions'


export default combineReducers({
  categories: categories,
  leads: leads,
  lists: lists,
  auth: auth
})

function categories(state=[], action) {
  switch (action.type) {
    case FETCH_CATEGORIES: 
     return [...action.categories]
    default: 
     return state
  }
}

function leads(state=[], action) {
  switch (action.type) {
     case FETCH_LEADS:
       return [{...action.leads}]
    default: 
     return state
  }
}

function lists(state=[], action) {
  switch (action.type) {
     case FETCH_LISTS:
       return [{...action.lists}]
     case ADD_LIST: 
      return {...state,
      lists: [...state.lists, action.list]}
     case DELETE_LIST:
      const lists = state.lists.filter(list => list.id !== action.list.id)
      return {...state, lists}
    default: 
     return state
  }
}

function auth(state = {}, action) {
  switch (action.type) {
    case 'LOGIN_USER':
      return {...action.user}
    case 'LOGOUT_USER':
      return {}
    default:
      return state
  }
}
