import React from "react";
import PropTypes from "prop-types";
import { NavLink, Redirect, Link, withRouter } from "react-router-dom";
import { logoutUser, clearMessage } from "../actions";
import { connect } from "react-redux";
import {
  Header,
  Image,
  Dropdown
} from "semantic-ui-react";

const styleBar = {
  overflow: 'hidden',
  // backgroundColor: '#5400E8',
  margin: '10px',
  height: '50px',
  textAlign: 'center',
  verticalAlign: 'middle',
  position: 'fixed',
  opacity: '1',
  zIndex: '1',
  top: '0',
  width: '100%'
}

const styleLinkLeft = {
  float: 'left',
  display: 'block',
  color: '#6200EE',
  textAlign: 'center',
  padding: '14px 16px',
  fontSize: '17px',
  textDecoration: 'none',
  marginLeft: '20px'
}

const styleLinkRight = {
  float: 'right',
  display: 'block',
  color: '#6200EE',
  textAlign: 'center',
  padding: '14px 16px',
  fontSize: '17px',
  textDecoration: 'none'
}

class Navbar extends React.Component {

  handleLogout = () => {
    this.props.logoutUser()
    localStorage.removeItem('token')
    this.props.history.push('/')
    // redirect to landing page
  }

  handleClearMessage = () => {
    this.props.clearMessage()
  }

  render() {
    return (
      <div style={styleBar}>
        <div className='style-links' style={{marginRight:'60px'}}>
        <NavLink onClick={this.handleClearMessage} style={styleLinkLeft} to="/">
          Home
        </NavLink>
        <NavLink onClick={this.handleClearMessage} style={styleLinkLeft} to="/dashboard">
          Dashboard
        </NavLink>
        {/* <NavLink onClick={this.handleClearMessage} style={styleLinkLeft} to="/profile">
          My Profile
        </NavLink> */}

<Dropdown>
    <Dropdown.Menu>
      <Dropdown.Item text='New' />
      <Dropdown.Item text='Open...' description='ctrl + o' />
      <Dropdown.Item text='Save as...' description='ctrl + s' />
    </Dropdown.Menu>
  </Dropdown>
  <Dropdown>
        <Image style={{display: 'inline-block', float: 'right', margin: '10px'}} circular size='mini' src={this.props.auth.user.img_url}></Image>
        </Dropdown>
          <NavLink onClick={this.handleClearMessage} style={styleLinkRight} to="/profile">
          {this.props.auth.user.first_name}
        </NavLink>
        <NavLink style={styleLinkRight} onClick={() => this.handleLogout()}>
          Logout
        </NavLink>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    auth: state.auth
  };
};

export default connect(
  mapStateToProps,
  {logoutUser, clearMessage}
)(withRouter(Navbar));
