import React, { Component } from "react";
import {
  Grid,
  Image,
  Table,
  Icon,
  Button,
  Pagination,
  Header,
  Modal,
  Form,
  Search,
  Segment,
  Label,
  Popup,
  Sticky,
  Visibility,
  Dropdown
} from "semantic-ui-react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import _ from "lodash";
import Navbar from "./Navbar";
import MailForm from "./MailForm";
import {
  deleteList,
  deleteListLead,
  addLeadNote,
  updateSearch
} from "../actions";
import PropTypes from "prop-types";
import FilterLeads from "./FilterLeads";
import { GridList } from "@material-ui/core";

const filterOptions = [
  {
    key: "name",
    text: "First Name",
    value: "first_name"
  },
  {
    key: "last_name",
    text: "Last Name",
    value: "last_name"
  },
  {
    key: "position",
    text: "Position",
    value: "position"
  },
  {
    key: "status",
    text: "Status",
    value: "status"
  },
  {
    key: "next_steps",
    text: "Next Steps",
    value: "next_steps"
  }
];

const styleMetrics = {
  borderWidth: "2px",
  // marginRight: '30px',
  borderRadius: "10px",
  borderColor: "#CECFD0",
  borderStyle: "solid",
  margin: "20px",
  padding: "20px",
  // width: "10px",
  height: "150px",
  boxShadow: "10px 10px 15px -6px rgba(67,66,93,0.68)"
  // padding: "20px"
};

const styleImage = {
  position: "relative",
  top: "-40px",
  opacity: 0.8
};

const styleButton = {
  borderRadius: "30px",
  color: "white",
  backgroundColor: "#6200EE",
  padding: "14px",
  paddingRight: "20px",
  paddingLeft: "20px"
};

const statusArray = [
  { key: "10", text: "Meeting booked", value: "Meeting booked" },
  {
    key: "25",
    text: "Met with decision maker",
    value: "Met with decision maker"
  },
  { key: "50", text: "Proposal sent", value: "Proposal sent" },
  { key: "90", text: "Verbal confirmation", value: "Verbal confirmation" },
  { key: "100", text: "Sale closed", value: "Sale closed" },
  {
    key: "notfit",
    text: "Not fit for business",
    value: "Not fit for business"
  },
  { key: "incorrect", text: "Incorrect contact", value: "Incorrect contact" },
  {
    key: "contact",
    text: "Contact at later date",
    value: "Contact at later date"
  }
];

// ! Search bar

const resultRenderer = ({ first_name }) => <Label content={first_name} />;

resultRenderer.propTypes = {
  first_name: PropTypes.string,
  last_name: PropTypes.string,
  position: PropTypes.string
};

class Dashboard extends Component {
  // UPDATE DATA WITH LISTS
  state = {
    column: null,
    data: [],
    direction: null,
    activePage: 1,
    statusInput: "",
    nextStepsInput: "",
    commentsInput: "",
    // totalLeadCount: 0,
    // totalMeetingsBooked: 0,
    // totalNotContacted: 0,
    filter: "",
    searchSelection: ""
  };

  // ! ////////////////////////////////////// CLASS COMPONENT FUNCTIONS ////////////////////////////////////////////////////////////

  componentDidMount() {
    // this.formattedListArray();
  }

  // formattedListArray = () => {
