import React, { Component } from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Image,
  Message,
  Segment
} from "semantic-ui-react";
import { Link, withRouter, NavLink } from "react-router-dom";
import { connect } from "react-redux";
import { registerUser } from "../actions";

// const styleColumn = {
//   float: "left",
//   width: "50%",
//   padding: "14px"
//   // margin: '10px'
// };

const styleForm = {
  float: "right",
  width: "50%",
  padding: "20px",
  paddingLeft: "20px"
};

class NewUserForm extends Component {
  state = {
    firstName: "",
    lastName: "",
    email: "",
    password: ""
  };

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    this.props.registerUser(this.state, this.props.history);
  };

  render() {
    console.log("new user form", this.state);
    return (
      <div className="new-user-form">
        <Grid>
          {/* <Grid.Column style={styleColumn}>
            <Image
              src="https://scontent-ort2-2.xx.fbcdn.net/v/t1.15752-9/70899389_2519172824806568_6105558141708009472_n.png?_nc_cat=109&_nc_oc=AQl7M70Sg3NpW0mxFQ2HiBBb2vaWR7OI91ePG62W4zv7EYNgY-_2YGp5fZ2Vio2dVgE&_nc_ht=scontent-ort2-2.xx&oh=83ca814288ac01ffb3a5c2a9f90c506f&oe=5DFE3575"
              width="100%"
              height="100%"
            />
          </Grid.Column> */}
          <Grid.Column
            style={styleForm}
            textAlign="center"
            verticalAlign="middle"
          >
            <Header as="h2" style={{ color: "#03DAC6" }} textAlign="center">
              Create a New Account
            </Header>
            <Form size="large" onSubmit={this.handleSubmit}>
              <Segment>
                <Form.Input
                  onChange={this.handleChange}
                  name="firstName"
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="First Name"
                />
                <Form.Input
                  onChange={this.handleChange}
                  name="lastName"
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="Last Name"
                />
                <Form.Input
                  onChange={this.handleChange}
                  name="email"
                  fluid
                  icon="envelope open icon"
                  iconPosition="left"
                  placeholder="Email"
                />
                <Form.Input
                  onChange={this.handleChange}
                  name="password"
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />
                <Button
                  style={{ backgroundColor: "#03DAC6", color: "white" }}
                  fluid
                  size="large"
                >
                  Create Account
                </Button>
                <NavLink to="/" activeClassName="hurray">
                  Back to Login
                </NavLink>
              </Segment>
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    registerUser: (formData, history) =>
      dispatch(registerUser(formData, history))
  };
}

export default connect(
  null,
  mapDispatchToProps
)(withRouter(NewUserForm));
