import { addList, addLead } from "../actions";
import Navbar from "./Navbar";

const uuidv1 = require("uuid/v1");

const styleDropdown = {
  width: "40%"
};

class SearchResults extends Component {
  state = {
    listId: "",
    leadSelection: "",
    company: "",
    website: "",
    newListName: "",
    leadsArray: []
