                    {Object.keys(lists).length > 1 ? (
                      <div>
                        <Modal.Header as="h2">
                          Select an Existing List:
                        </Modal.Header>
                        <Modal.Actions>
                          <Dropdown
                            onChange={this.handleDropdown}
                            name="listId"
                            style={styleDropdown}
                            placeholder="Select list..."
                            fluid
                            disabled={this.state.newListName ? true : false}
                            selection
                            options={listArray}
                          />
                          <Modal.Header as="h2">
                            Select an Existing List:
                          </Modal.Header>
                          <Form.Input
                            placeholder="Create new list..."
                            onChange={this.handleChange}
                            name="newListName"
                          />
                          <Modal.Header as="h2"></Modal.Header>
                          <Button
                            onClick={this.handleSubmit}
                            basic
                            color="blue"
                            inverted
                          >
                            <Icon name="add" /> Add Lead to List
                          </Button>
                        </Modal.Actions>
                      </div>
                    ) : (
                      <div>
                        <Modal.Header as="h2">Create A New List</Modal.Header>
                        <Modal.Actions>
                          <Form.Input
                            placeholder="Create new list..."
                            onChange={this.handleChange}
                            name="newListName"
                          />
                          <Button
                            onClick={this.handleSubmit}
                            basic
                            color="blue"
                            inverted
                          >
                            <Icon name="add" /> Add Lead to List
                          </Button>
                        </Modal.Actions>
                      </div>
                    )}
                  </Modal>
                </Table.HeaderCell>
              </Table.Row>
            ) : null}
            <Table.Row>
              <Table.HeaderCell>First Name</Table.HeaderCell>
              <Table.HeaderCell>Last Name</Table.HeaderCell>
              <Table.HeaderCell>Email</Table.HeaderCell>
              <Table.HeaderCell>Position</Table.HeaderCell>
              <Table.HeaderCell>Company</Table.HeaderCell>
              <Table.HeaderCell>Confidence</Table.HeaderCell>
              <Table.HeaderCell>Save Lead</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>{tableRow}</Table.Body>
          <Table.Footer>
            <Table.Row>
              <Table.HeaderCell colSpan="3">
                <Pagination
                  boundaryRange={0}
                  defaultActivePage={1}
                  ellipsisItem={"..."}
                  firstItem={{ content: <Icon name='angle double left' />, icon: true }}
                  lastItem={{ content: <Icon name='angle double right' />, icon: true }}
                  prevItem={{ content: <Icon name='angle left' />, icon: true }}
                  nextItem={{ content: <Icon name='angle right' />, icon: true }}
                  siblingRange={1}
                  totalPages={10}
                  onPageChange={(event, { activePage }) => this.handlePageChange(activePage)}
                />
                {/* <Menu floated="right" pagination>
                  <Menu.Item as="a" icon>
                    <Icon name="chevron left" />
                  </Menu.Item>
                  <Menu.Item as="a">1</Menu.Item>
                  <Menu.Item as="a">2</Menu.Item>
                  <Menu.Item as="a">3</Menu.Item>
                  <Menu.Item as="a">4</Menu.Item>
                  <Menu.Item as="a" icon>
                    <Icon name="chevron right" />
                  </Menu.Item>
                </Menu> */}
              </Table.HeaderCell>
            </Table.Row>
          </Table.Footer>
        </Table>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    leads: state.leads,
    lists: state.lists,
    auth: state.auth
  };
};

const mapDispatchToProps = dispatch => {
  return {
    addList: listName => {
      dispatch(addList(listName));
    },
    addLead: (leadsArray, company, website, listId, newListName, userId) => {
      dispatch(
        addLead(leadsArray, company, website, listId, newListName, userId)
      );
    }
  };
};

// Connect this component to all returns for the company search
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(SearchResults);
